$("#contactForm").validator().on("submit", function(event) {
    if (event.isDefaultPrevented()) {
        // handle the invalid form...
        formError();
        submitMSG(false, "Did you fill in the form properly?");
    } else {
        // everything looks good!
        event.preventDefault();
        submitForm();
    }
});


function submitForm() {
    // Initiate Variables With Form Content
    var name = $("#name").val();
    var email = $("#email").val();
    var msg_subject = $("#msg_subject").val();
    var message = $("#message").val();

    var $form = $("#contactForm");
    var action = $form.attr('action') || '/contact';
    var token = $form.find('input[name="_token"]').val();

    // show loader state
    var $btn = $form.find('#form-submit');
    var originalBtnHtml = $btn.html();
    $btn.prop('disabled', true).html('<i class="fa fa-spinner fa-spin"></i> Sending...');

    $.ajax({
        type: "POST",
        url: action,
        data: {
            _token: token,
            name: name,
            email: email,
            subject: msg_subject,
            message: message
        },
        dataType: 'json'
    }).done(function(resp) {
        if (resp && resp.status === 'success') {
            formSuccess();
            submitMSG(true, resp.message || 'Message Submitted!');
        } else {
            formError();
            submitMSG(false, (resp && resp.message) ? resp.message : 'There was an error sending the message.');
        }
    }).fail(function(xhr) {
        formError();
        var msg = 'There was an error sending the message.';
        if (xhr && xhr.responseJSON && xhr.responseJSON.message) msg = xhr.responseJSON.message;
        submitMSG(false, msg);
    }).always(function() {
        $btn.prop('disabled', false).html(originalBtnHtml);
    });
}

function formSuccess() {
    $("#contactForm")[0].reset();
    submitMSG(true, "Message Submitted!")
}

function formError() {
    $("#contactForm").removeClass().addClass('shake animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function() {
        $(this).removeClass();
    });
}

function submitMSG(valid, msg) {
    if (valid) {
        var msgClasses = "small text-left text-success";
    } else {
        var msgClasses = "small text-left text-danger";
    }
    $("#msgSubmit").removeClass().addClass(msgClasses).text(msg);
}